import { LitElement, css, html } from "lit";
import { customElement, property, state } from "lit/decorators.js";
import { GLOBAL_CSS } from "../styles/styles";
import type {
  ConnectionInfoResponse,
  StartIssuingRequest,
  StartIssuingResponse,
} from "../types";
import { useConfig } from "../utils";

import "./ssi-card";
import "./ssi-qrcode";

/**
 * This element shows a qr code for the invitation id and checks if the code was scanned yet.
 *
 * @slot The children of this element are shown when the qr code was scanned. Intended for a back button of some kind.
 */
@customElement("ssi-transfer-proof")
export class SsiTransferProof extends LitElement {
  @property({ type: String }) token = "";
  @property({ type: String }) credentialType: "ACAPY" | "OID4VCI" = "ACAPY";
  @property({ type: String }) issuerSlug?: string;
  @property({ type: String }) qrCodeLogoSrc?: string;
  @property({ type: String }) belowQrCodeSrc?: string;
  @property({ type: String }) walletName: string = "ssimply";

  @state() currentConnection?: StartIssuingResponse;
  @state() isActive?: boolean;
  @state() isError = false;

  timer?: number;
  config = useConfig();

  async firstUpdated(changedProperties: Map<string, unknown>) {
    if (changedProperties.has("token")) {
      this._initConnection();
    }
  }

  disconnectedCallback() {
    window.clearInterval(this.timer);
    super.disconnectedCallback();
  }

  render() {
    if (this.isError) {
      return html`<ssi-card id="root">
        <div>
          <h3 class="heading">Fehler</h3>
          <p class="subheading">
            Beim Laden der Verbindung ist ein Fehler aufgetreten.
          </p>
        </div>
        <slot></slot>
      </ssi-card>`;
    }
    if (!this.currentConnection || this.isActive === undefined) {
      return html`<div class="loading">
        <img
          src="data:image/svg+xml;base64,"
          alt=""
        />
      </div>`;
    }
    if (this.isActive) {
      return html`
        <ssi-card id="root">
          <div>
            <h3 class="heading">Nachweis hinzufügen</h3>
            <p class="subheading">
              Bestätigen Sie die Anfrage in der ${this.walletName}-App, um den
              Nachweis zum Wallet hinzuzufügen.
            </p>
            <img
              id="scanned-image"
              src="data:image/svg+xml;base64,"
              alt=""
            />
          </div>
          <button
            id="again"
            @click="${this._handleAgainClicked}"
            title="Nachweis nochmal ausstellen"
          >
            <img
              src="data:image/svg+xml;base64,"
              alt=""
            />
          </button>
          <slot></slot>
        </ssi-card>
      `;
    }
    return html`
      <ssi-card id="root">
        <div>
          <h3 class="heading">Nachweis übertragen</h3>
          <p class="subheading">
            Sannen Sie den QR-Code mit der ${this.walletName}-App, um den
            Nachweis zu übertragen.
          </p>
        </div>
        <div class="qrcode">
          <div class="qrcode__container">
            <ssi-qrcode
              content=${this.currentConnection?.connectionInvitation
                .invitation_url}
            ></ssi-qrcode>
            <img
              src="${this.qrCodeLogoSrc ??
              "data:image/png;base64,"}"
              class="qrcode__wallet"
              alt=""
            />
          </div>
          <img
            src="${this.belowQrCodeSrc ??
            "data:image/svg+xml;base64,"}"
            class="qrcode__text"
            alt=""
          />
        </div>
        <div class="actions">
          <div class="actions__item">
            <a
              href=${this.currentConnection?.connectionInvitation
                .invitation_url}
            >
              <img
                src="data:image/svg+xml;base64,"
                alt=""
            /></a>
            <a
              href=${this.currentConnection?.connectionInvitation
                .invitation_url}
            >
              <p>Öffnen Sie die ${this.walletName}-App</p>
            </a>
          </div>
          <div class="actions__arrow">
            <img
              src="data:image/svg+xml;base64,"
              alt=""
            />
          </div>
          <div class="actions__item">
            <img
              src="data:image/svg+xml;base64,"
              alt=""
            />
            <p>Tippen Sie auf das Scannen-Icon</p>
          </div>
          <div class="actions__arrow">
            <img
              src="data:image/svg+xml;base64,"
              alt=""
            />
          </div>
          <div class="actions__item">
            <img
              src="data:image/svg+xml;base64,"
              alt=""
            />
            <p>Scannen Sie den QR-Code</p>
          </div>
        </div>
      </ssi-card>
    `;
  }

  private _initConnection = async () => {
    await this._getConnection();
    await this._checkIfActive();
    if (!this.isActive) {
      this.timer = window.setInterval(this._checkIfActive, 2000);
    }
  };

  private _checkIfActive = async () => {
    if (this.currentConnection) {
      const { active } = await fetch(
        new URL(
          `/v1/connectionInfo?transactionId=${this.currentConnection.transactionId}`,
          this.config.baseUrl
        )
      ).then(async (res) => {
        if (res.ok) {
          return (await res.json()) as ConnectionInfoResponse;
        }
        this.isError = true;
        return { active: false };
      });
      this.isActive = active;
      if (active) {
        clearInterval(this.timer);
      }
    }
  };

  private _getConnection = async () => {
    const stored = sessionStorage.getItem(`connection-${this.token}`);
    if (stored) {
      this.currentConnection = JSON.parse(stored);
    } else {
      const body: StartIssuingRequest = { token: this.token };
      const url = new URL(`/v1/startIssuing`, this.config.baseUrl);
      url.search = new URLSearchParams({
        credentialType: this.credentialType,
        ...(this.issuerSlug ? { issuerSlug: this.issuerSlug } : {}),
      }).toString();
      const connection = await fetch(url, {
        method: "POST",
        body: JSON.stringify(body),
        headers: {
          "content-type": "application/json",
        },
      }).then(async (res) => {
        if (res.ok) {
          return (await res.json()) as StartIssuingResponse;
        }
        this.isError = true;
        return null;
      });
      if (connection) {
        this.currentConnection = connection;
        sessionStorage.setItem(
          `connection-${this.token}`,
          JSON.stringify(connection)
        );
      }
    }
  };

  private _handleAgainClicked = async () => {
    sessionStorage.removeItem(`connection-${this.token}`);
    this.isError = false;
    await this._initConnection();
  };

  static styles = css`
    ${GLOBAL_CSS}

    #root {
      position: relative;
    }

    #scanned-image {
      margin: 2rem auto;
    }

    .heading {
      font-size: 1.875rem;
      font-weight: 600;
      text-align: center;
    }

    .subheading {
      text-wrap: balance;
      text-align: center;
      max-width: 400px;
      margin-inline: auto;
      margin-block-start: 0.25rem;
    }

    .qrcode {
      display: flex;
      flex-direction: column;
      align-items: center;
      gap: 1rem;
    }

    .qrcode__container {
      position: relative;
      aspect-ratio: 1 / 1;
      padding: 2.5rem;
      border-radius: 2.5rem;
      max-width: 20rem;
      box-shadow: 0px 0px 20px 0px rgba(0, 0, 0, 0.25);
    }

    .qrcode__wallet {
      position: absolute;
      left: 50%;
      top: 50%;
      width: 3rem;
      height: 3rem;
      transform: translateX(-50%) translateY(-50%);
    }

    .qrcode__text {
      height: 2rem;
    }

    .actions {
      display: grid;
      grid-template-columns: minmax(0, 1fr);
      gap: 1.25rem;
      padding: 1.5rem;
    }

    .actions__item {
      display: flex;
      flex-direction: row;
      align-items: center;
      gap: 0.75rem;
    }

    .actions__item a {
      flex-shrink: 0;
    }

    .actions__item img {
      width: 2rem;
      height: 2rem;
    }

    .actions__item p {
      text-wrap: balance;
    }

    .actions__arrow {
      display: none;
      height: 2rem;
      align-items: center;
      justify-content: center;
    }

    .actions__arrow img {
      width: 1.75rem;
      transform: rotate(90deg);
    }

    .loading {
      display: flex;
      justify-content: center;
      align-items: center;
    }

    #again {
      position: absolute;
      top: 4px;
      right: 4px;
      background: transparent;
      border: none;
      cursor: pointer;
      height: 28px;
      width: 28px;
      padding: 4px;
      border-radius: 4px;
      transition: background-color 0.2s ease;
    }

    #again img {
      opacity: 0.4;
    }

    #again:hover {
      background-color: hsl(0 0 0% / 0.05);
    }

    @keyframes rotate {
      from {
        transform: rotate(0deg);
      }
      to {
        transform: rotate(360deg);
      }
    }

    .loading img {
      opacity: 0.2;
      animation: 1s infinite linear rotate;
    }

    @media (min-width: 640px) {
      .actions__item {
        flex-direction: column;
        max-width: 180px;
        margin-inline: auto;
      }

      .actions {
        grid-template-columns: 1fr auto 1fr auto 1fr;
      }

      .actions__arrow {
        display: flex;
      }

      .actions__arrow img {
        transform: rotate(0);
      }

      .actions__item p {
        text-align: center;
      }
    }
  `;
}

declare global {
  interface HTMLElementTagNameMap {
    "ssi-transfer-proof": SsiTransferProof;
  }
}
